import React, { useEffect, useState } from 'react'

export default function LoginPage() {
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
  
    const handleSubmit = (e) => {
      e.preventDefault();
      console.log('İsim:', name);
      console.log('E-posta:', email);
      console.log('Şifre:', password);
    };

    useEffect(() => {
     document.title = "ForendonBlog | Giriş"
    }, [])
    


    return (
      <div className="min-h-screen bg-gray-100 flex flex-col justify-center items-center">

        <div className="mb-6">
          <img
            src={require("../assetes/nexarapng.png")}
            alt="Logo"
            className="h-20 w-20"
          />
        </div>
  
  
        <div className="bg-white p-8 rounded-lg shadow-lg w-full max-w-md">
          <h2 className="text-2xl font-bold mb-6 text-center">Giriş Yap</h2>
  
          <form onSubmit={handleSubmit}>
      
            <div className="mb-4">
              <label className="block text-gray-700 font-bold mb-2" htmlFor="name">
                İsim
              </label>
              <input
                type="text"
                id="name"
                value={name}
                onChange={(e) => setName(e.target.value)}
                className="w-full px-4 py-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-green-500"
                placeholder="İsminizi girin"
              />
            </div>
  
          
            <div className="mb-4">
              <label className="block text-gray-700 font-bold mb-2" htmlFor="email">
                E-posta
              </label>
              <input
                type="email"
                id="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)} 
                className="w-full px-4 py-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-green-500"
                placeholder="E-posta adresinizi girin"
              />
            </div>
  
        
            <div className="mb-6">
              <label className="block text-gray-700 font-bold mb-2" htmlFor="password">
                Şifre
              </label>
              <input
                type="password"
                id="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                className="w-full px-4 py-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-green-500"
                placeholder="Şifrenizi girin"
              />
            </div>
  
          
            <div className="text-center">
              <button
                type="submit"
                className="w-full bg-green-500 text-white py-2 rounded-md hover:bg-green-700 transition duration-300"
              >
                Giriş Yap
              </button>
              <p className='pt-4 text-red-400'>Bir hata oluştu</p>
              <p className='pt-4 text-[#000000a6]'>Hesabınız yok mu ? <span className='text-blue-500'>Kayıt ol</span></p>
            </div>
          </form>
        </div>
      </div>
    );
}
