import { CalendarIcon, UserIcon } from '@heroicons/react/24/outline';
import React, { useEffect, useState } from 'react';
import { ChevronLeftIcon, ChevronRightIcon } from '@heroicons/react/20/solid';
import axios from 'axios';

export default function SearchPage() {
  const [blogs, setBlogs] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [blogsPerPage] = useState(10); // Sayfa başına blog sayısı
  const [searchTerm, setSearchTerm] = useState(''); // Arama terimi

  useEffect(() => {
    document.title = 'ForendonBlog | Ana Sayfa';

    const fetchAllBlogs = async () => {
      const getBlogs = await axios.get("http://localhost:7007/api/posts");
      const blogsData = getBlogs.data;

      setBlogs(blogsData);
    };

    fetchAllBlogs();
  }, []);

  const formatDate = (dateString) => {
    const options = { day: 'numeric', month: 'long', year: 'numeric' };
    const date = new Date(dateString);
    return date.toLocaleDateString('tr-TR', options);
  };

  // Arama terimine göre blogları filtrele
  const filteredBlogs = blogs.filter(blog =>
    blog.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
    blog.desc.toLowerCase().includes(searchTerm.toLowerCase())
  );

  // Şu anki blogların başlangıç ve bitiş indekslerini hesapla
  const indexOfLastBlog = currentPage * blogsPerPage;
  const indexOfFirstBlog = indexOfLastBlog - blogsPerPage;
  const currentBlogs = filteredBlogs.slice(indexOfFirstBlog, indexOfLastBlog);

  // Sayfa değiştirme fonksiyonları
  const paginateNext = () => {
    if (currentPage < Math.ceil(filteredBlogs.length / blogsPerPage)) {
      setCurrentPage(currentPage + 1);
    }
  };

  const paginatePrev = () => {
    if (currentPage > 1) {
      setCurrentPage(currentPage - 1);
    }
  };

  return (
    <div className="flex items-center justify-center w-screen min-h-screen">
      <div className="flex flex-col items-center justify-center w-screen min-h-screen">
       
        <div className="flex w-screen items-center justify-center mt-8 pb-10">
  <div className="relative w-[50%] flex items-center justify-center flex-col">
  <h1 className="text-3xl font-bold text-green-600  mb-4">
    Blog Arat
</h1>
    <input
      type="text"
      placeholder="Blogl ara..."
      value={searchTerm}
      onChange={(e) => setSearchTerm(e.target.value)}
      className="w-[50%] py-2.5 pl-3 pr-5 text-gray-900 bg-white border border-green-300 rounded-lg shadow focus:outline-none focus:ring-2 focus:ring-green-500 focus:border-green-500 transition duration-150 ease-in-out"
    />
  </div>
</div>  

     
<div className='flex flex-wrap justify-center'>
  {currentBlogs.map((blog) => (
    <div className="flex flex-col items-center w-full sm:w-1/2 md:w-1/3 lg:w-1/3 p-4" key={blog.id}>
      <p className="text-green-600 font-semibold pb-2">{blog?.cate?.toUpperCase()}</p>
      <h1 className="text-3xl font-black hover:underline text-center">{blog?.title.slice(0, 30)}</h1>
      <div className="flex flex-row pt-5 gap-4">
        <p className="flex flex-row gap-2">
          <UserIcon className="h-6 w-6 text-black" />
          Yazar: <span className="hover:underline">{blog.username}</span>
        </p>
        <p className="flex flex-row gap-2">
          <CalendarIcon className="h-6 w-6 text-black" />
          {formatDate(blog.date)}
        </p>
      </div>
      <img className="mt-5 w-full h-[200px] rounded-xl bg-black text-white object-cover" src={blog.img} alt={blog.title} />
      <p className="w-full pt-5 text-xl font-extralight text-center">{blog?.desc.slice(0, 120)}...</p>
      <button className="px-4 py-2 mt-5 bg-green-600 text-white rounded-lg hover:bg-green-700 focus:outline-none focus:ring-2 focus:ring-green-500 focus:ring-opacity-50">
        Devamını oku
      </button>
    </div>
  ))}
</div>





        <div className="flex items-center w-[90%] justify-between bg-white px-4 py-3 sm:px-6 mt-auto">
          <div className="flex justify-between w-full">
            <button
              onClick={paginatePrev}
              className="relative inline-flex items-center rounded-md border border-gray-300 bg-white px-4 py-2 text-sm font-medium text-gray-700 hover:bg-gray-50"
            >
              Previous
            </button>
            <span className="text-sm text-gray-700">
              Toplam <span className="font-medium">{filteredBlogs.length}</span> blogdan
              <span className="font-medium"> {indexOfFirstBlog + 1} - {Math.min(indexOfLastBlog, filteredBlogs.length)}</span> arası gösteriliyor.
            </span>
            <button
              onClick={paginateNext}
              className="relative inline-flex items-center rounded-md border border-gray-300 bg-white px-4 py-2 text-sm font-medium text-gray-700 hover:bg-gray-50"
            >
              Next
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}
