import { CalendarIcon, UserIcon } from '@heroicons/react/24/outline';
import React, { useEffect } from 'react';

export default function HomePage() {
  useEffect(() => {
    document.title = 'NexaraBlog | Ana Sayfa';
  }, []);

  const blogsPosts = [
    {
      id: 1,
      title: 'Teknoloji Dünyasında Son Trendler',
      description: 'Teknolojideki en yeni gelişmeleri ve trendleri keşfedin.Teknolojideki en yeni gelişmeleri ve trendleri keşfedin.Teknolojideki en yeni gelişmeleri ve trendleri keşfedin.Teknolojideki en yeni gelişmeleri ve trendleri keşfedin.Teknolojideki en yeni gelişmeleri ve trendleri keşfedin. Teknolojideki en yeni gelişmeleri ve trendleri keşfedin.',
      author: 'Ahmet Yılmaz',
      date: '4 ekim 2024',
      img: 'https://images.wallpaperscraft.com/image/single/laptop_keyboard_glow_170138_1920x1080.jpg',
      category: 'Teknoloji'
    },
    {
      id: 2,
      title: 'Seyahat Tutkunları İçin En İyi Rotalar',
      description: 'Dünyanın dört bir yanındaki eşsiz destinasyonları keşfedin.',
      author: 'Elif Demir',
      date: '4 ekim 2024',
      img: 'https://example.com/seyahat.jpg',
      category: 'Seyahat'
    },
    {
      id: 3,
      title: 'Sağlıklı ve Lezzetli Yemek Tarifleri',
      description: 'Kendi mutfağınızda yapabileceğiniz nefis tarifler.',
      author: 'Murat Koç',
      date: '4 ekim 2024',
      img: 'https://example.com/yemek.jpg',
      category: 'Yemek'
    },
    {
      id: 4,
      title: 'Sağlıkla İlgili En Yeni Bilgiler',
      description: 'Sağlıklı bir yaşam tarzı için önemli tavsiyeler ve ipuçları.',
      author: 'Ayşe Kaya',
      date: '4 ekim 2024',
      img: 'https://example.com/saglik.jpg',
      category: 'Sağlık'
    },
    {
      id: 5,
      title: 'Finans Yönetimi: Paranızı Nasıl Yönetmelisiniz?',
      description: 'Kişisel finansınızı geliştirmek için faydalı ipuçları.',
      author: 'Kemal Yıldırım',
      date: '4 ekim 2024',
      img: 'https://example.com/finans.jpg',
      category: 'Finans'
    },
    {
      id: 6,
      title: 'Eğitimde Dijitalleşme ve Yeni Yöntemler',
      description: 'Geleceğin eğitim trendleri ve yeni öğrenme metotları.',
      author: 'Zeynep Şahin',
      date: '4 ekim 2024',
      img: 'https://example.com/egitim.jpg',
      category: 'Eğitim'
    },
    {
      id: 7,
      title: 'Son Dakika Haberleri ve Gelişmeler',
      description: 'Dünya genelindeki en güncel ve önemli haberler.',
      author: 'Ali Öztürk',
      date: '4 ekim 2024',
      img: 'https://example.com/haberler.jpg',
      category: 'Haberler'
    }
  ];
  

  return (
    <div className="flex items-center justify-center w-screen min-h-screen ">
      <div className="flex flex-col items-center justify-center w-screen min-h-screen">
        <div className='w-screen h-16'></div>
        {blogsPosts.map((blog) => (
          <div className="w-[90%] min-h-screen flex flex-col items-center pb-[100px]" key={blog.id}>
             <p className='text-green-600'>{blog.category.toUpperCase()}</p>
            <h1 className='text-6xl font-black'>{blog.title}</h1>
            <div className='flex flex-row pt-5 gap-4'>
            <p className='flex flex-row gap-2'><UserIcon className="h-6 w-6 text-black" />{blog.author}</p>
            <p className='flex flex-row gap-2'><CalendarIcon className="h-6 w-6 text-black" />{blog.date}</p>
            </div>
            <img className='pt-5 w-auto h-[600px]' src={blog.img} alt={blog.title} />
            <p className='w-[50%] pt-5 text-xl font-extralight text-center'>{blog.description}...</p>
          </div>
        ))}
      </div>
    </div>
  );
}
