import { CalendarIcon, UserIcon } from '@heroicons/react/24/outline';
import React, { useEffect, useState } from 'react';
import { ChevronLeftIcon, ChevronRightIcon } from '@heroicons/react/20/solid';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

export default function SearchPage() {
  const [blogs, setBlogs] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [blogsPerPage] = useState(10); // Sayfa başına blog sayısı
  const [searchTerm, setSearchTerm] = useState(''); // Arama terimi
  const navigate = useNavigate();

  useEffect(() => {
    document.title = 'ForendonBlog | Ana Sayfa';

    const fetchAllBlogs = async () => {
      const getBlogs = await axios.get("http://localhost:7007/api/posts/");
      const blogsData = getBlogs.data;
      const reversedData = [...blogsData].reverse();
      setBlogs(reversedData)
    };

    fetchAllBlogs();
  }, []);

  const formatDate = (dateString) => {
    const options = { day: 'numeric', month: 'long', year: 'numeric' };
    const date = new Date(dateString);
    return date.toLocaleDateString('tr-TR', options);
  };

  // Arama terimine göre blogları filtrele
  const filteredBlogs = blogs.filter(blog =>
    blog.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
    blog.desc.toLowerCase().includes(searchTerm.toLowerCase())
  );

  // Şu anki blogların başlangıç ve bitiş indekslerini hesapla
  const indexOfLastBlog = currentPage * blogsPerPage;
  const indexOfFirstBlog = indexOfLastBlog - blogsPerPage;
  const currentBlogs = filteredBlogs.slice(indexOfFirstBlog, indexOfLastBlog);

  // Sayfa değiştirme fonksiyonları
  const paginateNext = () => {
    if (currentPage < Math.ceil(filteredBlogs.length / blogsPerPage)) {
      setCurrentPage(currentPage + 1);
    }
  };

  const paginatePrev = () => {
    if (currentPage > 1) {
      setCurrentPage(currentPage - 1);
    }
  };

  return (
    <div className="flex items-center justify-center w-screen min-h-screen">
      <div className="flex flex-col items-center justify-center w-screen min-h-screen">
       
        <div className="flex w-screen items-center justify-center mt-8 pb-10">
  <div className="relative w-[50%] flex items-center justify-center flex-col">
  <h1 className="text-3xl font-bold text-green-600  mb-4">
    Blog Ara
</h1>
    <input
      type="text"
      placeholder="Blog ara..."
      value={searchTerm}
      onChange={(e) => setSearchTerm(e.target.value)}
      className="w-[50%] py-2.5 pl-3 pr-5 text-gray-900 bg-white border border-green-300 rounded-lg shadow focus:outline-none focus:ring-2 focus:ring-green-500 focus:border-green-500 transition duration-150 ease-in-out"
    />
  </div>
</div>  

     
<div className='flex flex-wrap  w-[90%] pb-[100px]'>
  {currentBlogs.map((blog) => (
    <div className="flex flex-col items-center w-full sm:w-1/1 md:w-1/2 lg:w-1/3 p-4 pt-10" key={blog.id}>
      <p className="text-green-600 font-semibold pb-2">{blog?.cate?.toUpperCase()}</p>
      <h1 className="text-2xl font-black hover:underline text-center">{blog?.title.length > 30 ? blog?.title.slice(0, 25) + "..." : blog?.title }
      </h1>
      <div className="flex flex-row pt-5 gap-4">
        <p className="flex flex-row gap-2">
          <UserIcon className="h-6 w-6 text-black" />
          Yazar: <span className="hover:underline">{blog.username}</span>
        </p>
        <p className="flex flex-row gap-2">
          <CalendarIcon className="h-6 w-6 text-black" />
          {formatDate(blog.date)}
        </p>
      </div>
      <img className="mt-5 w-full h-[250px] rounded-xl bg-black text-white object-cover" src={blog.img} alt={blog.title} />
      <p className="w-full pt-5 text-lg font-extralight text-center">{blog?.desc.slice(0, 120)}...</p>
      <button className="px-4 py-2 mt-5 bg-green-600 text-white rounded-lg hover:bg-green-700 focus:outline-none focus:ring-2 focus:ring-green-500 focus:ring-opacity-50" onClick={() => navigate(`/blog/${blog.id}`)}>
        Devamını oku
      </button>
    </div>
  ))}
</div>





<div className="flex items-center w-[90%] justify-between bg-white px-4 py-3 sm:px-6 mt-auto">
      <div className="flex-1 flex justify-between sm:hidden">
        <a
          href="#"
          className="relative inline-flex items-center rounded-md border border-gray-300 bg-white px-4 py-2 text-sm font-medium text-gray-700 hover:bg-gray-50"
        >
          Previous
        </a>
        <a
          href="#"
          className="relative ml-3 inline-flex items-center rounded-md border border-gray-300 bg-white px-4 py-2 text-sm font-medium text-gray-700 hover:bg-gray-50"
        >
          Next
        </a>
      </div>
      <div className="hidden sm:flex sm:flex-1 sm:items-center sm:justify-between">
        <div>
          <p className="text-sm text-gray-700">
            <span className="font-medium">97</span> sonuçtan 
            <span className="font-medium">1</span> ile <span className="font-medium">10</span> arası gösteriliyor
          </p>
        </div>
        <div>
          <nav aria-label="Pagination" className="isolate inline-flex -space-x-px rounded-md shadow-sm">
            <a
              href="#"
              className="relative inline-flex items-center rounded-l-md px-2 py-2 text-gray-400 ring-1 ring-inset ring-gray-300 hover:bg-gray-50 focus:z-20 focus:outline-offset-0"
            >
              <span className="sr-only">Previous</span>
              <ChevronLeftIcon aria-hidden="true" className="h-5 w-5" />
            </a>
            <a
              href="#"
              aria-current="page"
              className="relative z-10 inline-flex items-center bg-green-600 px-4 py-2 text-sm font-semibold text-white focus:z-20 focus-visible:outline focus-visible:outline-2 focus-visible:outline-offset-2 focus-visible:outline-indigo-600"
            >
              1
            </a>
            <a
              href="#"
              className="relative inline-flex items-center px-4 py-2 text-sm font-semibold text-gray-900 ring-1 ring-inset ring-gray-300 hover:bg-gray-50 focus:z-20 focus:outline-offset-0"
            >
              2
            </a>
            <a
              href="#"
              className="relative hidden items-center px-4 py-2 text-sm font-semibold text-gray-900 ring-1 ring-inset ring-gray-300 hover:bg-gray-50 focus:z-20 focus:outline-offset-0 md:inline-flex"
            >
              3
            </a>
            <span className="relative inline-flex items-center px-4 py-2 text-sm font-semibold text-gray-700 ring-1 ring-inset ring-gray-300 focus:outline-offset-0">
              ...
            </span>
            <a
              href="#"
              className="relative hidden items-center px-4 py-2 text-sm font-semibold text-gray-900 ring-1 ring-inset ring-gray-300 hover:bg-gray-50 focus:z-20 focus:outline-offset-0 md:inline-flex"
            >
              8
            </a>
            <a
              href="#"
              className="relative inline-flex items-center px-4 py-2 text-sm font-semibold text-gray-900 ring-1 ring-inset ring-gray-300 hover:bg-gray-50 focus:z-20 focus:outline-offset-0"
            >
              9
            </a>
            <a
              href="#"
              className="relative inline-flex items-center px-4 py-2 text-sm font-semibold text-gray-900 ring-1 ring-inset ring-gray-300 hover:bg-gray-50 focus:z-20 focus:outline-offset-0"
            >
              10
            </a>
            <a
              href="#"
              className="relative inline-flex items-center rounded-r-md px-2 py-2 text-gray-400 ring-1 ring-inset ring-gray-300 hover:bg-gray-50 focus:z-20 focus:outline-offset-0"
            >
              <span className="sr-only">Next</span>
              <ChevronRightIcon aria-hidden="true" className="h-5 w-5" />
            </a>
          </nav>
        </div>
      </div>
    </div>
  </div>
    </div>
  );
}
