import { CalendarIcon, UserIcon } from '@heroicons/react/24/outline';
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

export default function BlogPage() {
    const { idblog } = useParams();
    const [blog, setBlogs] = useState(null);

    useEffect(() => {
        document.title = `${blog ? blog.title : "Forendon"}`;

        const fetchAllblogs = async () => {
            try {
                const getblogs = await axios.get(`http://localhost:7007/api/posts/${idblog}`);
                const blogsdata = getblogs.data;
                setBlogs(blogsdata);
            } catch (error) {
                console.error("Veriler çekilirken bir hata oluştu:", error);
            }
        };

        fetchAllblogs();
    }, [idblog]);

    const formatDate = (dateString) => {
        const options = { day: 'numeric', month: 'long', year: 'numeric' };
        const date = new Date(dateString);
        return date.toLocaleDateString('tr-TR', options);
    };

    return (
        <div className="flex items-center justify-center w-screen min-h-screen">
            <div className="flex flex-col w-screen min-h-screen">
                {/* Üst Kısım - Koyu Gri */}
                <div className="flex flex-col items-center justify-center flex-1 bg-[#1D1D1D]">
                    <div className='w-screen h-16'></div>
                    <div className="w-[90%] flex flex-col items-center pb-[100px]" key={blog?.id}>
                        <p className='text-green-600 font-semibold pb-2'>{blog?.cate?.toUpperCase()}</p>
                        <h1 className='text-6xl text-white font-black hover:underline'>{blog?.title}</h1>
                        <div className='flex flex-row pt-5 gap-4'>
                            <p className='flex flex-row gap-2 text-white'>
                                <UserIcon className="h-6 w-6 text-white" />
                                Yazar: <span className='hover:underline text-white'>{blog?.username}</span>
                            </p>
                            <p className='flex flex-row gap-2 text-white'>
                                <CalendarIcon className="h-6 w-6 text-white" />
                                {formatDate(blog?.date)}
                            </p>
                        </div>
                        <img className='mt-5 w-[80%] h-[600px] rounded-xl bg-black text-white' src={blog?.img} alt={blog?.title} />
                        <p className='w-[50%] pt-5 text-xl font-extralight text-white'>{blog?.desc}</p>
                    </div>
                </div>

                {/* Alt Kısım - Beyaz */}
                <div className="flex-1 bg-white"></div>
            </div>
        </div>
    );
}
