import React, { useState } from "react";
import ReactQuill from "react-quill"; 
import "react-quill/dist/quill.snow.css"; 

export default function BlogCreatePage() {
    const [title, setTitle] = useState("");
    const [description, setDescription] = useState("");
    const [category, setCategory] = useState("");
    const [image, setImage] = useState(null);
  
    const handleImageUpload = (e) => {
      setImage(e.target.files[0]);
    };
  
    const handleSaveDraft = () => {
      console.log({
        title,
        description,
        category,
        image,
      });
    };
  
    return (
      <div className="max-w-[85%] h-screen mx-auto p-6 justify-center gap-5 flex-col  ">
      
  
  
    <h1 className="text-3xl font-semibold mb-4 ">Create a New Blog</h1>
    <div className="mb-4">
          <label className="block text-gray-700 font-medium mb-2">Blog Title</label>
          <input
            type="text"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            className="w-full px-4 py-2 border rounded-md focus:outline-none focus:ring-2 focus:ring-blue-400"
            placeholder="Enter blog title"
          />
        </div>
  
     
        <div className="mb-4">
          <label className="block text-gray-700 font-medium mb-2">Description</label>
          <ReactQuill
            value={description}
            onChange={setDescription}
            className="border rounded-md h-[250px] bg-white"
            placeholder="Write your blog description"
          />
        </div>



  
    <div className="mt-10 pt-5">
          <label className="block text-gray-700 font-medium mb-2">Category</label>
          <div className="flex flex-col">
            <label className="flex items-center mb-2">
              <input
                type="radio"
                value="Tech"
                checked={category === "Tech"}
                onChange={(e) => setCategory(e.target.value)}
                className="mr-2"
              />
              Tech
            </label>
            <label className="flex items-center mb-2">
              <input
                type="radio"
                value="Lifestyle"
                checked={category === "Lifestyle"}
                onChange={(e) => setCategory(e.target.value)}
                className="mr-2"
              />
              Lifestyle
            </label>
            <label className="flex items-center mb-2">
              <input
                type="radio"
                value="Education"
                checked={category === "Education"}
                onChange={(e) => setCategory(e.target.value)}
                className="mr-2"
              />
              Education
            </label>
          </div>
        </div>
  
       
        <div className="mb-4">
          <label className="block text-gray-700 font-medium mb-2">Upload Image</label>
          <input
            type="file"
            onChange={handleImageUpload}
            className="block w-full text-sm text-gray-500 file:mr-4 file:py-2 file:px-4 file:rounded-full file:border-0 file:bg-green-50 file:text-green-600 hover:file:bg-green-100"
          />
        </div>
        <button
          onClick={handleSaveDraft}
          className="bg-green-500 text-white py-2 px-4 rounded-md hover:bg-green-600"
        >
          Save Draft
        </button>
  
      
     
      </div>
    );
}
