import { CalendarIcon, UserIcon } from '@heroicons/react/24/outline';
import React, { useEffect, useState } from 'react';
import { ChevronLeftIcon, ChevronRightIcon } from '@heroicons/react/20/solid';
import axios from 'axios';

export default function HomePage() {
    const [blogs, setBlogs] = useState([]);
    const [authors, setAuthors] = useState({}); // Yazar bilgilerini saklamak için state

    useEffect(() => {
        document.title = 'NexaraBlog | Ana Sayfa';

        const fetchBlogsAndAuthors = async () => {
            try {
                const getBlogs = await axios.get("http://localhost:7007/api/posts");
                setBlogs(getBlogs.data);

                // Tüm yazarları almak için bir istek yap
                const authorsData = await Promise.all(getBlogs.data.map(blog => getAuthorData(blog.uid)));
                // Yazar bilgilerini bir nesne olarak sakla
                const authorsMap = {};
                getBlogs.data.forEach((blog, index) => {
                    authorsMap[blog.uid] = authorsData[index]; // Kullanıcı ID'si ile yazar bilgilerini eşleştir
                });
                setAuthors(authorsMap);
            } catch (error) {
                console.error("Error fetching blogs:", error);
            }
        };

        fetchBlogsAndAuthors();
    }, []);

    const getAuthorData = async (userId) => {
        try {
            const response = await axios.get(`http://localhost:7007/api/auth/${userId}`);
            return response.data; 
        } catch (error) {
            console.error("Error fetching author data:", error);
            return null; 
        }
    };

    return (
        <div className="flex items-center justify-center w-screen min-h-screen ">
            <div className="flex flex-col items-center justify-center w-screen min-h-screen">
                <div className='w-screen h-16'></div>
                {blogs.map((blog) => (
                    <div className="w-[90%] min-h-screen flex flex-col items-center pb-[100px]" key={blog.id}>
                        <p className='text-green-600 font-semibold pb-2'>{blog?.cate?.toUpperCase()}</p>
                        <h1 className='text-6xl font-black hover:underline'>{blog.title}</h1>
                        <div className='flex flex-row pt-5 gap-4'>
                            <p className='flex flex-row gap-2'>
                                <UserIcon className="h-6 w-6 text-black" />
                                Yazar: 
                                <span className='hover:underline'>{authors[blog.uid] ? authors[blog.uid].username : "Loading..."}</span>
                            </p>
                            <p className='flex flex-row gap-2'>
                                <CalendarIcon className="h-6 w-6 text-black" />
                                {blog.date}
                            </p>
                        </div>
                        <img className='mt-5 w-[80%] h-[600px] rounded-xl bg-black text-white' src={blog.img} alt={blog.title} />
                        <p className='w-[50%] pt-5 text-xl font-extralight '>{blog.desc}...</p>
                        <button className="px-4 py-2 mt-5 bg-green-600 text-white rounded-lg hover:bg-green-700 focus:outline-none focus:ring-2 focus:ring-green-500 focus:ring-opacity-50">
                            Devamını oku
                        </button>
                    </div>
                ))}
                {/* Pagination component can go here */}
            </div>
        </div>
    );
}
